/* * 文 件 名： * 版权(c) 2018-xxx公司 xxx项目组： * 版 本 号： */package com.csdj.entry;/** * @author 易富军 * @version 1.0 * @date 2020/03/13 下午 11:00 * @description: 学生类 */public class Pom {    private Long id;    private String name;    private int age;    public Pom() {    }    public Pom(Long id, String name, int age) {        this.id = id;        this.name = name;        this.age = age;    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public int getAge() {        return age;    }    public void setAge(int age) {        this.age = age;    }}