/* * 文 件 名： * 版权(c) 2018-xxx公司 xxx项目组： * 版 本 号： */package com.hystrix;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.cloud.client.circuitbreaker.EnableCircuitBreaker;import org.springframework.cloud.netflix.hystrix.dashboard.EnableHystrixDashboard;import org.springframework.cloud.openfeign.EnableFeignClients;/** * @version 1.0 * @author： 易富军 * Modified By： 修改人姓名(如果有其他人修改时增加这三项) * Modified Date: 修改日期 * @EnableCircuitBreaker 声明启用数据监控 * @EnableHystrixDashboard 声明启用可视化的数据监控 */@SpringBootApplication@EnableFeignClients@EnableCircuitBreaker@EnableHystrixDashboardpublic class hystrixApplication {    public static void main(String[] args) {        SpringApplication.run(hystrixApplication.class,args);    }}